import React, { useEffect, useState } from 'react';

const GalleryGrid = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [itemVisibility, setItemVisibility] = useState({});

  useEffect(() => {
    fetch('http://localhost:3001/work')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const itemsWithBase64Images = data.map(item => ({
          ...item,
          image: arrayBufferToBase64(item.image.data),
        }));
        setItems(itemsWithBase64Images);
        setItemVisibility(itemsWithBase64Images.reduce((acc, item) => {
          acc[item.id] = true; // Implicit că toate itemele sunt vizibile la început
          return acc;
        }, {}));
        setLoading(false);
      })
      .catch(error => {
        console.error('There was an error fetching the items!', error);
        setError(error);
        setLoading(false);
      });
  }, []);

  const arrayBufferToBase64 = (buffer) => {
    let binary = '';
    const bytes = new Uint8Array(buffer);
    const len = bytes.byteLength;
    for (let i = 0; i < len; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return window.btoa(binary);
  };

  const toggleItemVisibility = (id) => {
    setItemVisibility(prevVisibility => ({
      ...prevVisibility,
      [id]: !prevVisibility[id], // Schimbă vizibilitatea pentru itemul respectiv
    }));
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>There was an error: {error.message}</p>;
  }

  return (
    <div className="gallery-grid">
      <h1>Gallery</h1>
      <div className="gallery-container">
        {items.map(item => (
          itemVisibility[item.id] && (
            <div key={item.id} className="gallery-item">
              <div className="gallery-image-container">
                <img src={`data:image/jpeg;base64,${item.image}`} alt={item.title} className="gallery-image" />
              </div>
              <div className="gallery-info">
                <h3>{item.title}</h3>
                <p>{item.description}</p>
              </div>
              <div className="gallery-controls">
                <button onClick={() => toggleItemVisibility(item.id)}>
                  {itemVisibility[item.id] ? 'Hide' : 'Show'} Item
                </button>
              </div>
            </div>
          )
        ))}
      </div>
    </div>
  );
};

export default GalleryGrid;
