import React, { useEffect, useState } from 'react';

const WorkGrid = () => {
  const [works, setWorks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Înlocuiește cu URL-ul corect pentru backend-ul tău
    fetch('http://localhost:3001/work')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setWorks(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('There was an error fetching the works!', error);
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>There was an error: {error.message}</p>;
  }

  return (
    <div className="work-grid">
      <h1>Works</h1>
      <div className="grid-container">
        {works.map(work => (
          <div key={work.id} className="grid-item">
            <img src={`data:image/jpeg;base64,${work.image}`} alt={work.title} className="work-image" />
            <div className="work-info">
              <h3>{work.title}</h3>
              <p>{work.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WorkGrid;
