import React, { useEffect, useState } from 'react';

const GalleryGrid = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:3001/work')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        const itemsWithBase64Images = data.map(item => ({
          ...item,
          image: arrayBufferToBase64(item.image.data),
          status: item.status || 'visible', // Asigură-te că există un câmp status
        }));
        setItems(itemsWithBase64Images);
        setLoading(false);
      })
      .catch(error => {
        console.error('There was an error fetching the items!', error);
        setError(error);
        setLoading(false);
      });
  }, []);

  const arrayBufferToBase64 = (buffer) => {
    let binary = '';
    const bytes = new Uint8Array(buffer);
    const len = bytes.byteLength;
    for (let i = 0; i < len; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return window.btoa(binary);
  };

  const toggleItemVisibility = async (id) => {
    try {
      // Schimbă statusul local al elementului
      const updatedItems = items.map(item => 
        item.id === id 
          ? { ...item, status: item.status === 'visible' ? 'hidden' : 'visible' } 
          : item
      );
      setItems(updatedItems);

      // Trimite actualizarea către server
      const response = await fetch(`http://localhost:3001/work/${id}/toggle`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      // Dacă este necesar, actualizează starea locală cu datele actualizate
      // De exemplu, poți reîncărca lista completă sau actualiza doar itemul modificat
    } catch (error) {
      console.error('There was an error updating the item status!', error);
      // Revină la starea anterioară sau afișează mesaj de eroare
    }
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>There was an error: {error.message}</p>;
  }

  return (
    <div className="gallery-grid">
      <h1>Gallery</h1>
      <div className="gallery-container">
        {items.map(item => (
          <div key={item.id} className="gallery-item">
            <div className="gallery-image-container">
              <img src={`data:image/jpeg;base64,${item.image}`} alt={item.title} className="gallery-image" />
            </div>
            <div className="gallery-info">
              <h3>{item.title}</h3>
              <p>{item.description}</p>
            </div>
            <div className="gallery-controls">
              <button onClick={() => toggleItemVisibility(item.id)}>
                {item.status === 'visible' ? 'Hide' : 'Show'} Item
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GalleryGrid;
