import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const AddWorkForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [link, setLink] = useState('');
  const [status] = useState('hidden'); // Status implicit

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description || !image || !link) {
      alert('Please fill out all fields.');
      return;
    }

    // Creare obiect FormData pentru a trimite imaginea
    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('link', link);
    formData.append('status', status);
    formData.append('image', image);

    try {
      const response = await fetch('http://localhost:3001/work', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const result = await response.json();
      console.log('Work added successfully:', result);
      // Resetează formularul sau trimite utilizatorul undeva
    } catch (error) {
      console.error('There was an error adding the work:', error);
    }
  };

  return (
    <div>
      <h1>Add New Work</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="image">Image:</label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
            required
          />
        </div>
        <div>
          <label htmlFor="link">Link:</label>
          <input
            type="url"
            id="link"
            value={link}
            onChange={(e) => setLink(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Work</button>
      </form>
    </div>
  );
};

export default AddWorkForm;
