import React, { useState } from 'react';

const AddWorkForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);
  const [link, setLink] = useState('');
  const [status] = useState('hidden'); // Status implicit
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description || !image || !link) {
      setErrorMessage('Please fill out all fields.');
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('description', description);
    formData.append('image', image);
    formData.append('clientUrl', link);
    formData.append('status', status);

    try {
      const response = await fetch('http://localhost:3001/work', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        // Check if the error is due to a duplicate title or another issue
        const errorResponse = await response.json();
        if (response.status === 400 && errorResponse.message.includes('duplicate')) {
          setErrorMessage('A work with this title already exists.');
        } else {
          setErrorMessage('A work with this title already exists.');
          setErrorMessage('.');
        }
        return;
      }

      const result = await response.json();
      console.log('Work added successfully:', result);

      // Reset the form after a successful submission
      setTitle('');
      setDescription('');
      setImage(null);
      setLink('');
      setErrorMessage(''); // Clear any previous error messages
    } catch (error) {
      setErrorMessage('There was an error adding the work.');
      console.error('There was an error adding the work:', error);
    }
  };

  return (
    <div className="add-work-form-container">
      <h1>Add New Work</h1>
      {errorMessage && <p className="error-message">{errorMessage}</p>} {/* Display error message */}
      <form onSubmit={handleSubmit} className="add-work-form">
        <div className="form-group">
          <label htmlFor="title">Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            className="form-input"
          />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            className="form-textarea"
          />
        </div>
        <div className="form-group">
          <label htmlFor="image">Image:</label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={(e) => setImage(e.target.files[0])}
            required
            className="form-file-input"
          />
        </div>
        <div className="form-group">
          <label htmlFor="link">Link:</label>
          <input
            type="url"
            id="link"
            value={link}
            onChange={(e) => setLink(e.target.value)}
            required
            className="form-input"
          />
        </div>
        <button type="submit" className="submit-button">Add Work</button>
      </form>
    </div>
  );
};

export default AddWorkForm;
