// WelcomePage.js
import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import './WelcomePage.css'; // Asigură-te că stilurile sunt corecte

const WelcomePage = () => {
  const [greeting, setGreeting] = useState('');
  const [timeOfDay, setTimeOfDay] = useState('');
  const history = useHistory();

  useEffect(() => {
    // Obține ora curentă
    const now = new Date();
    const hours = now.getHours();

    // Determină momentul din zi
    if (hours < 12) {
      setGreeting('Good Morning');
    } else if (hours < 18) {
      setGreeting('Good Afternoon');
    } else {
      setGreeting('Good Evening');
    }

    // Afișează ora curentă
    setTimeOfDay(`It is currently ${hours % 12 || 12}:${now.getMinutes().toString().padStart(2, '0')} ${hours < 12 ? 'AM' : 'PM'}.`);
  }, []);

  const handleGoHome = () => {
    history.push('/'); // Redirecționează către pagina principală
  };

  return (
    <div className="welcome-container">
      <h1>{greeting}, User!</h1>
      <p>{timeOfDay}</p>
      <button className="home-button" onClick={handleGoHome}>Go to Home</button>
    </div>
  );
};

export default WelcomePage;
